(function (ga) {
	var s = document.getElementsByTagName("script");
	var p = false;
	for(var i=0,l=s.length;i<l;i++){
		var n = s[i];
		if(n.getAttribute('src') && n.getAttribute('src').indexOf("//m.chevrolet.com.cn/js/mz_ga_code.")!=-1){
			if(n.getAttribute('data-disable-default-pageview')=="0" ){
				p = true;
			}
			var mzid = n.getAttribute("data-mzid");
			if(mzid!=null && mzid!='' && !isNaN(mzid)) window.__MIAOZHEN_CiQ_ID__ = parseInt(mzid);
			break;
		}
		
	}
	ga('create', 'UA-30403543-5', 'auto');//把cookies锁定于根域名及根目录下
	ga('require', 'displayfeatures');
	if(p){ga('send', 'pageview');_sgma("send","pageview");}//default tracking page view once.
})(ga);


